import React,  { useState } from "react";

import {EntriesLog, EntryItem} from './EntriesLog';
import {ChangeDateButtons} from "./ChangeDateButtons";


// the api rest framework
import axios from "axios";

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      entryList: [],
      modal: false,
      startDate:"",
      endDate:"",
      activeItem: {
        title: "",
        description: "",
        completed: false,
      },
    };
    // this.date= new Date()
  }

// const entry_list = []

  // we set this in order to render the component 
  componentDidMount() {
    this.getDates();
    this.refreshList();
    console.log("rendering list")
  }
  // componentNeedsUpdate() {
  //   this.getDates();
  //   this.refreshList();
  //   console.log("rendering list")
  // }

  // componentNeedsUpdate



  refreshList = () => {

    axios
    .get("/api/entries/")
    .then((res) => { 
      this.setState({entryList: res.data})
    })
    .catch((err) => console.log(err));
  }

  getDates() {
    const now = new Date(2021,6,8)
    const now_month = now.getMonth()
    const now_year = now.getFullYear()
    var next_month_date
    // const second_date_month = date
    if (now.getMonth() == 11) {
      next_month_date = new Date(now.getFullYear() + 1, 0, 1);
    } else {
      next_month_date = new Date(now.getFullYear(), now.getMonth() + 1, 1);
    }
    const next_month_month = next_month_date.getMonth()
    const next_month_year = next_month_date.getFullYear()

    this.setState({startDate:`${now_year}-${now_month+1}-01`})
    this.setState({endDate:`${next_month_year}-${next_month_month+1}-01`})



  }

  renderItems = () => {
    const filteredList = this.state.entryList.filter(function (list) {
      // console.log("list_item "+list)
      return list.date >= start_date  && list.date < end_date
    })

    return filteredList

    .map(
      (entry) => (

        <EntryItem
          entry_type={entry.entry_type}
          description={entry.description}
          category={entry.category}
          date={entry.date}
          amount={entry.amount}
        />
    )
  )
  }


  render() {

    const rendered_list = this.state.entryList
    const start_date = this.state.startDate
    const end_date = this.state.endDate

    return (
    <div className="app_body">



      <ChangeDateButtons/>
      <div className="table_div">
            <table className="table table-hover table-stripped">
                <thead>
                    <tr className="log_table_item_head">
                        <th scope="col">Entry type</th>
                        <th scope="col">Description</th>
                        <th scope="col">Category</th>
                        <th scope="col">Date</th>
                        <th scope="col">Amount</th>
                    </tr>
                </thead>
                <tbody>

                  {this.renderItems()}
                </tbody>
                </table>

            </div>
      {/* <EntriesLog>
        {console.log(rendered_list)}
        {console.log("start: "+start_date)}
        {console.log("end: "+end_date)}


      </EntriesLog> */}
    </div>
    )
  }



}

export {AppBody};
