import React,  { useState } from "react";

import {EntriesLog, EntryItem} from './EntriesLog';
import {ChangeDateButtons} from "./ChangeDateButtons";

// the api rest framework
import axios from "axios";



// const entry_list = []



function AppBody() {

  const [entryList, setEntryList] = useState([])
const [month, setMonth] = useState("")
const [year, setYear] = useState("")

React.useEffect( () => {
  setInterval(() => {

    axios
    .get("/api/entries/")
    .then((res) => { 
      setEntryList(res.data)
    })
    .catch((err) => console.log(err));
  }, 2000)
  })



const date = new Date()
  // console.log(date.getFullYear());
  // console.log(date.getMonth());
// setMonth(date.getMonth())



  // we get or refresh list


  var startDate = "2022-01-01"
  var endDate = "2022-02-01"







  return (
    <div className="app_body">
      <ChangeDateButtons/>

      <EntriesLog>

        {entryList
          .filter(function (list) {
            return list.date >= startDate  && list.date < endDate
          })
          .map(
            (entry) => (
              <EntryItem
                entry_type={entry.entry_type}
                description={entry.description}
                category={entry.category}
                date={entry.date}
                amount={entry.amount}
              />
          )
        )}

      </EntriesLog>
    </div>
  );

}

export {AppBody};
