import React,  { useState } from "react";

import {EntriesLog, EntryItem} from './EntriesLog';
import {ChangeDateButtons} from "./ChangeDateButtons";

// the api rest framework
import axios from "axios";

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      entryList: [],
      modal: false,
      startDate:"",
      endDate:"",
      activeItem: {
        title: "",
        description: "",
        completed: false,
      },
    };
    // this.date= new Date()
  }

// const entry_list = []

  // we set this in order to render the component 
  componentDidMount() {
    this.refreshList();
    this.getDates();

    console.log("rendering list")
  }

  // componentNeedsUpdate

// const [entryList, setEntryList] = useState([])
// const [month, setMonth] = useState("")
// const [year, setYear] = useState("")


  refreshList = () => {

    axios
    .get("/api/entries/")
    .then((res) => { 
      this.setState({todoList: res.data})
    })

    .catch((err) => console.log(err));
  }

  getDates() {
    const now = new Date()
    const now_month = now.getMonth() + 1
    const now_year = now.getFullYear()
    // const second_date_month = date
    if (now.getMonth() == 11) {
      var next_month_date = new Date(now.getFullYear() + 1, 0, 1);
    } else {
        var next_month_date = new Date(now.getFullYear(), now.getMonth() + 1, 1);
    }
    const next_month_month = next_month_date.getMonth()
    const next_month_year = next_month_date.getFullYear()

    this.setState({startDate:`${now_year}-${now_month}-01`})
    this.setState({endDate:`${next_month_year}-${next_month_month}-01`})



  }



  render() {
    return (
    <div className="app_body">
      {console.log(this.state.startDate)}
      {console.log(this.state.endDate)}
      {console.log(this.state.entryList)}

      <ChangeDateButtons/>

      <EntriesLog>
        {this.state.entryList
          .filter(function (list) {
            return list.date >= this.state.startDate  && list.date < this.state.endDate
          })
          .map(
            (entry) => (
              <EntryItem
                entry_type={entry.entry_type}
                description={entry.description}
                category={entry.category}
                date={entry.date}
                amount={entry.amount}
              />
          )
        )}

      </EntriesLog>
    </div>
    )
  }



}

export {AppBody};
