import React,  { useState } from "react";

import {EntriesLog, EntryItem} from './EntriesLog';

// the api rest framework
import axios from "axios";



// const entry_list = []


function AppBody() {

  let [entryList, setEntryList] = useState([])
  var [month, setMonth] = useState("")
  var [year, setYear] = useState("")


  // we get or refresh list
  const refreshList = () => {
    axios
      .get("/api/entries/")
      .then((res) => { 
        setEntryList(res.data)
      })
      .catch((err) => console.log(err));
  };

  var startDate = "2022-01-01"
  var endDate = "2022-02-01"
  {refreshList()}

  return (
    <div className="app_body">
      <EntriesLog>

        {entryList
          .filter(function (list) {
            return list.date >= startDate  && list.date < endDate
          })
          .map(
            (entry) => (
              <EntryItem
                entry_type={entry.entry_type}
                description={entry.description}
                category={entry.category}
                date={entry.date}
                amount={entry.amount}
              />
          )
        )}

      </EntriesLog>
    </div>
  );

}

export {AppBody};
